settings.custom_css:
body {
     overflow-x:hidden;
} 
.elementor-shape-top {
     bottom: -1px;
     animation: wave 22s cubic-bezier( 0.36, 0.45, 0.63, 0.53) infinite;
     width: 200%;
 } 
@keyframes wave {
   0% {
     margin-left: 0%;
   }
     50% {
     margin-left: -60%;
   }
   100% {
     margin-left: 0%;
   }

body {
     overflow-x:hidden;
} 
.elementor-shape-bottom {
     bottom: -1px;
     animation: wave 22s cubic-bezier( 0.36, 0.45, 0.63, 0.53) infinite;
     width: 200%;
 } 
@keyframes wave {
   0% {
     margin-left: 0%;
   }
     50% {
     margin-left: -130%;
   }
   100% {
     margin-left: 0%;
   }
}
